import { Component } from "@angular/core";

@Component({
  selector: 'app-about-page',
  templateUrl: './about-page.component.html',
  styleUrls: ['./about-page.component.html']
})
export class AboutPageComponent {
  description: string;
  editedDescription: string;

  constructor(private aboutService: AboutSevice) {}

  ngOnInit(): void {
    
    this.descriptionService.getDescription().subscribe((data) => {
      this.description = data;
      this.editedDescription = data; // Initialize editedDescription
    });
  }

  editDescription() {
    // Enable editing mode
    this.editMode = true;
  }

  saveDescription() {
    // Update the edited description locally
    this.description = this.editedDescription;

    // Save the updated description
    this.descriptionService.updateDescription(this.editedDescription).subscribe(() => {
      this.editMode = false; // Disable editing mode after saving
    });
  }
}
