import { Component } from '@angular/core';
import { Url } from '../models/url.model';
@Component({
  selector: 'app-upsert-dialog',
  templateUrl: './upsert-dialog.component.html',
  styleUrls: ['./upsert-dialog.component.css'],
  providers: [],
})
export class UpsertDialogComponent {
  url: Url;

  showModal: boolean = false;

  //@Output() updateListEvent = new EventEmitter<ICreateTask>();

  constructor() {}

  displayModal() {
    this.showModal = !this.showModal;
  }

  addNewTask() {
    this.showModal = !this.showModal;
  }
}
<div class="add-button-div">
  <button type="button" class="btn btn-primary" (click)="displayModal()">Add Url</button>
</div>
<p-dialog
  header="Url"
  [(visible)]="showModal"
  [resizable]="false"
  [modal]="true"
  [draggable]="false"
>
<>
</p-dialog>
