import { Component, OnInit } from '@angular/core';
import { Url } from '../models/url.model';
import { UrlService } from '../services/url.service';
import { RedirectService } from '../services/redirect.service';
import { NgxRolesService } from 'ngx-permissions';
import { M } from '../services/permission.service';

@Component({
  selector: 'app-urls-table',
  templateUrl: './urls-table.component.html',
  styleUrls:['./urls-table.component.css'],
})
export class UrlsTableComponent implements OnInit {
  urls: Url[] = [];

  constructor(private urlService: UrlService,
              private redirectService: RedirectService,
              private roleService: NgxRolesService,
              private permissionsService: PermissionService) { }
  
  ngOnInit(): void {
    this.urlService.getAll().subscribe((resp) => {
     this.urls = resp;
    });

    debugger;
    const roles = this.permissionsService.getRoles();
    this.roleService.addRoles(roles);
    console.log(this.roleService.getRoles());
  }

  generateRedirect(slug: string) {
    return this.redirectService.generateRedirectUrl(slug);
  }
}
