import { Component } from '@angular/core';
import { Url } from '../models/url.model';
@Component({
  selector: 'app-upsert-dialog',
  templateUrl: './upsert-dialog.component.html',
  styleUrls: ['./upsert-dialog.component.css'],
  providers: [],
})
export class UpsertDialogComponent {
  url: Url;

  showModal: boolean = false;

  //@Output() updateListEvent = new EventEmitter<ICreateTask>();

  constructor() {}

  displayModal() {
    this.showModal = !this.showModal;
  }

  addNewTask() {
    this.showModal = !this.showModal;
  }
}
<div class="add-button-div">
  <button type="button" class="btn btn-primary" (click)="displayModal()">Add Url</button>
</div>
<p-dialog
  header="Url"
  [(visible)]="showModal"
  [resizable]="false"
  [modal]="true"
  [draggable]="false"
>
<<div class="modal" tabindex="-1" role="dialog">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary">Save changes</button>
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>>
</p-dialog>
