import { Component } from '@angular/core';
import { ActivatedRoute, ParamMap } from '@angular/router';
import { Url } from '../models/url.model';
import { UrlService } from '../services/url.service';
import { Roles } from '../models/roles';

@Component({
  selector: 'app-url-info-page',
  templateUrl: './url-info-page.component.html',
  styleUrls: ['./url-info-page.component.css']
})
export class UrlInfoPageComponent
{
  url: Url = {
    id: 0,
    userId: 0,
    userName: "",
    baseUrl: "",
    slug: "",
    createdAt: new Date(),
    modifiedAt: new Date(),
    description: "",
    domain: "",
  }

  areChangesMade: boolean = false;
  public Roles: Roles;public Roles = Roles;

  constructor(private route: ActivatedRoute,
              private urlService: UrlService) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe((params: ParamMap) => {
      const id = params.get('id');
      if (id !== null) {
        const idParam = +id; 
        this.urlService.get(idParam).subscribe((resp) => {
          this.url = resp;
        });
      } else console.log('Route Parameter ID is null.');
    });
  }

  onInputChange() {
    this.areChangesMade = true;
  }

  saveChanges() {

  }
}
