import { Component } from "@angular/core";
import { AboutService } from "../services/about.service";
import { Roles } from "../models/roles";
import { MyRoleService } from "../services/role.service";

@Component({
  selector: 'app-about-page',
  templateUrl: './about-page.component.html',
  styleUrls: ['./about-page.component.html']
})
export class AboutPageComponent {
  content: string;
  editMode: boolean = false;
  public Roles = Roles;

  constructor(private aboutService: AboutService,
              private roleService: MyRoleService) {}

  ngOnInit(): void {
    this.aboutService.getAboutContent().subscribe((data) => {
      this.content = data;
    });
  }

  isAdmin(): boolean {
    return this.roleService.hasRole(Roles.ADMIN);
  }

  editContent() {
    this.editMode = true;
  }

  saveDescription() {
    this.aboutService.storeAboutContent(this.content).subscribe(() => {
      this.editMode = false;
    });
  }
}
