import { Component, ElementRef, EventEmitter, Input, Output, ViewChild } from '@angular/core';
import { Url } from '../models/url.model';
import { UpsertUrlModel } from '../models/upsert.url.model';
import { UrlService } from '../services/url.service';

@Component({
  selector: 'app-upsert-dialog',
  templateUrl: './upsert-dialog.component.html',
  styleUrls: ['./upsert-dialog.component.css'],
  providers: [],
})
export class UpsertDialogComponent {
  @ViewChild('closeModal') closeModal: ElementRef

  url: UpsertUrlModel = {
    baseUrl: "",
    description: "",
  };

  @Input() isCreate: boolean;
  @Input() id: number;
  
  @Output() updateListEvent = new EventEmitter<Url>();
  constructor(private urlService: UrlService) {} 

  cancel() {

  }

  create() {
    

    this.closeModal.nativeElement.click()
  }

 
}
<div class="add-button-div">
  <button data-toggle="modal" data-target="#exampleModal" type="button" class="btn btn-primary">Add Url</button>
</div>
<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true" #myModal>
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Url</h5>
        <button #cloaseModal type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <div class="input-group mb-3 d-flex">
          <div class="input-group-prepend">
            <span class="input-group-text" id="basic-addon1">Description:</span>
          </div>
          <input type="text" class="custom-input" aria-label="Destination" aria-describedby="basic-addon1">
        </div>
        <div class="input-group mb-3 d-flex">
          <div class="input-group-prepend">
            <span class="input-group-text" id="basic-addon1">Destination url:</span>
          </div>
          <input type="text" class="custom-input" aria-label="Destination" aria-describedby="basic-addon1">
        </div>
    </div>
      <div class="modal-footer">
        <button type="button" (click)="cancel()" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
        <button type="button" (click)="create()" class="btn btn-primary">Create</button>
      </div>
    </div>
  </div>
</div>
