import { Component } from '@angular/core';
import { LoginModel } from '../models/login.model';
import { AuthService } from '../services/auth.service';
import { MyLocalStorageService } from '../services/my.local.storage.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-component',
  templateUrl: './login.component.html'
})
export class LoginComponent {
  loginModel = new LoginModel();

  constructor(private authService: AuthService,
              private localStorageService: MyLocalStorageService,
              private router: Router) {}

  submit() {
    this.authService.login(this.loginModel).subscribe(
      (resp) => {
        this.localStorageService.clear("jwt");
        this.localStorageService.setPair("jwt", resp.jwtToken)
        this.router.n
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Login</title>
</head>
<body>
    <div class="container mt-5">
        <div class="row justify-content-center">
            <div class="col-md-6">
                <div class="card">
                    <div class="card-header">
                        <h3 class="card-title">Login</h3>
                    </div>
                    <div class="card-body">
                        <form>
                            <div class="form-group">
                                <label for="username">Username</label>
                                <input [(ngModel)]="loginModel.username" name="username" type="text" class="form-control" id="username" placeholder="Enter username">
                            </div>
                            <div class="form-group">
                                <label for="password">Password</label>
                                <input [(ngModel)]="loginModel.password" name="password" type="password" class="form-control" id="password" placeholder="Enter password">
                            </div>
                            <button (click)="submit()" class="btn btn-primary btn-block">Login</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
</body>
</html>
