import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Url } from '../models/url.model';
@Component({
  selector: 'app-udialog',
  templateUrl: './upsert-dialog.component.html',
  styleUrls: ['./upsert-dialog.component.css'],
  providers: [],
})
export class DialogComponent {
  @Input() url: Url;

  showModal: boolean = false;

  //@Output() updateListEvent = new EventEmitter<ICreateTask>();

  constructor() {}

  displayModal() {
    this.showModal = !this.showModal;
  }

  addNewTask() {
    this.showModal = !this.showModal;
  }
}
<div class="add-button-div">
  <i class="pi pi-plus-circle c-circle" (click)="displayModal()"></i>
</div>
<p-dialog
  header="Url"
  [(visible)]="showModal"
  [resizable]="false"
  [modal]="true"
  [draggable]="false"
>
  <div class="input-div">
    <div>
      <span>Title:</span>
      <input maxlength="25" class="title-input" type="text" />
    </div>
    <div>
      <span>Description:</span>
      <textarea class="description-input" maxlength="110" rows="4"></textarea>
    </div>
  </div>
  <div>
    <button class="submit-btn" icon="pi pi-check" (click)="addNewTask()">
      <i class="pi pi-check"></i>
    </button>
  </div>
</p-dialog>
